/* persian entry bindings */

$('select.createboxInput[name*="Example.Language"]').change(function() {
/*   alert('You selected a language: '+$(this).val()); */
   if ($(this).val() == "Persian") {
      /* add persian keyboard setting */
alert("You selected person!");
      $(this).parent().parent().parent().find('textarea').attr("onkeydown","switchTextLang(this, event)").attr("dir","rtl").attr("onkeypress","keyEnterToPersian(this, event)").attr("lang","fa");
   } else {
      /* remove custom keyboard setting */
   alert("undoing keyboard");
$(this).parent().parent().parent().find('textarea').removeAttr("onkeydown").removeAttr("dir").removeAttr("onkeypress").removeAttr("lang");
   }
});

