    var event = document.all ? window.event : event;
    if (event.ctrlKey && event.altKey) {
        if (field.lang == "fa") {
            field.dir = "ltr";
            field.onkeypress = function() {
            }
        }
        else {
            field.dir = "rtl";
            field.onkeypress = function(event) {
                keyEnterToPersian(this, event);
            }
        }
    }
    field.lang = field.lang == "fa" ? "en" : "fa";
}

