$('#metanetexporttoowl a').click(function(){
     // create and submit a post form
     var form = $('<form method="post" action="http://metaphor.icsi.berkeley.edu/en/index.php?title=Special:ExportRDF"><textarea name="pages"></textarea><input type="submit" value="Export"/></form>';
     var data = $('#metanetexporttowl p.exportlist').html().replace(",","\n");
     form.find("textarea").html(data);
     $('#metanetexporttoown').append(form);
     form.find('input').click();
     return false;
});

