     $(document).ready(function() {
        // tab-ize metaphorfamilygraphs
        $('#metaphorfamilygraphs').tabs({selected: 1}); // default selection is the metaphor tab
        $('#metaphorrelationstabs').tabs({selected: 1});
     });
});

