    } UNION {
        ?tschema (mo:makesUseOfSchema&#124;mo:isSubcaseOfSchema)/(mo:makesUseOfSchema&#124;mo:isSubcaseOfSchema)/(mo:makesUseOfSchema&#124;mo:isSubcaseOfSchema)/(mo:makesUseOfSchema&#124;mo:isSubcaseOfSchema) ?sschema .
    }
}
}}

